
// set a globally accessible timer that can be reset w/i different functions
let countdownTimer;

// create a new NoSleep object that will be used in the click hanlder
const noSleep = new NoSleep();

// yes, a min is always 60 seconds, but this makes it hella easier
// to debug when a min is a lot less than 60 seconds ;)
const min = 60;

let minutes;
let burpees;
let total_minutes;
let current_minute;

/**
 * Burp!
 */
async function burp(){
  $("#newGame").prop("disabled",true);

  let end_beeps = 15;

  // todo - get this working so it actually resets when click "start" more than once
  // for now, it just reloads ¯\_(ツ)_/¯
  // stopGame();

  minutes = $('#minutes').val();
  total_minutes = minutes;
  burpees = 0;
  console.log('start: ' + total_minutes);

  $('#total_minutes').html(minutes);

  while (minutes > 0) {
    new ProgressCountdown(min);
    current_minute = total_minutes - minutes + 1;
    burpees = current_minute;
    $('#current_minute').html(current_minute);
    $('#burpees').html(burpees);
    console.log('minutes: ' + minutes);
    await sleep((min * 1000));
    beep();
    minutes--;
  }

  while (end_beeps > 0) {
    beep();
    await sleep((150));
    end_beeps--;
  }
  $("#newGame").prop("disabled",false);

}

// add click handler for the button to start the game and enable no sleep
$('#newGame').click(function (){
  burp();
  noSleep.enable();
})

// add click handler for the button to end the game and reset everything
$('#stopGame').click(function (){
  stopGame();
})

/**
 * use Audio element to play a sound from a local base64 encoded beep
 * thanks https://stackoverflow.com/questions/879152/how-do-i-make-javascript-beep
 */
function beep() {
  let snd = new Audio("data:audio/wav;base64,");
  snd.play();
}

/**
 * JS Sleep
 * Thanks https://stackoverflow.com/questions/951021/what-is-the-javascript-version-of-sleep#39914235
 * @param ms number of miliseconds to sleep for
 * @returns {Promise<unknown>}
 */
function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

/**
 * Update a progress bar timer
 * thanks https://stackoverflow.com/a/50846458
 * @param timeleft seconds left of timer
 * @constructor
 */
function ProgressCountdown(timeleft, board) {
  document.getElementById('timer').value = timeleft;
  document.getElementById('timer').setAttribute('max', timeleft);
  if(typeof countdownTimer === 'number'){
    clearInterval(countdownTimer);
  }
  countdownTimer = setInterval(function() {
    timeleft--;

    document.getElementById('timer').value = timeleft;

    if (timeleft <= 0) {
      console.log('done!')
      clearInterval(countdownTimer);
    }
  }, 1000);
}

